.square{
	position: relative; 
	width: 400px;
	height: 400px; 
	display: flex; 
	justify-content: center;
	align-items: center; 
	/* background-color: blue; */
}

.square span:nth-child(1) {
	position: absolute; 
	top: 0;
	left: 0; 
	width: 100%;
	height: 100%;
	border: 2px solid #978c77; 
	border-radius: 38% 62% 63% 37% / 41% 44% 56% 59%; 
	transition: 0.5s;
	animation: animate 6s linear infinite;
}

.square:hover span:nth-child(1) {
	border: none;
	background: rgba(0, 0, 0, 0.8);
}

.square span:nth-child(2) {
	position: absolute; 
	top: 0;
	left: 0; 
	width: 100%;
	height: 100%;
	border: 2px solid #978c77; 
	border-radius: 38% 62% 63% 37% / 41% 44% 56% 59%; 
	transition: 0.5s;
	animation: animate 4s linear infinite;
}

.square:hover span:nth-child(2) {
	border: none;
	background: rgba(0, 0, 0, 0.8);
}

.square span:nth-child(3) {
	position: absolute; 
	top: 0;
	left: 0; 
	width: 100%;
	height: 100%;
	border: 2px solid #978c77; 
	border-radius: 38% 62% 63% 37% / 41% 44% 56% 59%; 
	transition: 0.5s;
	animation: animate2 10s linear infinite;
}

.square:hover span:nth-child(3) {
	border: none;
	background: rgba(0, 0, 0, 0.8);
}

@keyframes animate {
	0%{
		transform: rotate(0deg);
	}
	100%{
		transform: rotate(360deg);
	}
}
@keyframes animate2 {
	0%{
		transform: rotate(360deg);
	}
	100%{
		transform: rotate(0deg);
	}
}

.content-stack{
	position: relative;
	padding: 40px 60px; 
	color: #000; 
	text-align: center;
	transition: 0.5s;
	z-index: 1000;
	white-space: normal;
}

