.gallery {
	display: flex;
	margin-left: 12vw;
	padding-right: 12vw;
}
.gallery__scroll{
	display: flex;
	align-items: center; 
	justify-content: center; 
	padding: 0;
	margin: 0; 	
}
.gallery__scroll-inner{
	font-family: halyard-display;
	font-size: 4em;
	color:var(--color-gallery-title)
}
.gallery__item {
	margin: 0 3vw;
	display: grid;
	grid-template-areas: '... ...' 
						 '... gallery-image'
						 '... ...' ;
	grid-template-columns: 8rem 41vmax;
	grid-template-rows: 8rem 28vmax 3rem;
	will-change: transform;
}
.gallery__item:nth-child(even) {
	padding-top: 10vh;
}

/************************* PROFIL PICTURE  **************************/

.gallery__item-profil {
	margin: 0 3vw;
	display: grid;
	grid-template-areas: '... ...' 
						 '... gallery-image'
						 '... ...' ;
	grid-template-columns: 8rem 41vmax;
	grid-template-rows: 8rem 28vmax 3rem;
	will-change: transform;
}
.gallery__item-img-profil {
	grid-area: gallery-image;
	width: 100%;
	height: 100%;
	overflow: hidden;
	position: relative;
	transform-origin: 50% 100%;
	border-radius:2%; 
	transition: 0.5s;
	will-change: transform;
}
.gallery__item-imginner-profil {
	background-size: cover;
    background-position: 50% 0;
    width: 100%;
	height: 100%;
	will-change: transform;
}
.gallery__item-caption-profil {
	grid-area: 1 / 1 / 4 / 3;
	display: grid;
	grid-template-areas: 'gallery-number gallery-title' 
						 'gallery-link ...'
						 'gallery-link gallery-tags' ;
	grid-template-columns: 8rem auto;
	grid-template-rows: 8rem auto 3rem;
}
.gallery__item-title-profil {
	grid-area: gallery-title;
	margin: 0;
	font-size: 4rem;
	font-size: clamp(2rem,5vw,4rem);
	color: var(--color-gallery-title);
	justify-self: center; 
}
.gallery__item-tags-profil {
	grid-area: gallery-tags;
	margin-top: 100px; 
	justify-self: center;
	font-size: 3rem;
	display: grid;
	grid-auto-flow: column;
	cursor: pointer;
	color: var(--color-gallery-title); 
	font-weight: 800;
	align-self: center;
}

/********************** IMG ******************************/

.gallery__item-img {
	grid-area: gallery-image;
	width: 100%;
	height: 100%;
	overflow: hidden;
	position: relative;
	transform-origin: 50% 100%;
	border-radius:2%; 
	transition: 0.5s;
	will-change: transform;
}
.gallery__item-img:hover{
	transform: translateY(-30px);
}

.gallery__item-imginner1 {
	background-size: cover ;
    background-position: 50% 50%;
    width: 100%;
	height: 100%;
	display: flex;
	align-items: center; 
	justify-content: center;
	background-color: black; 
	color: #fff; 
	font-size: 5em; 
}
.gallery__item-imginner2 {
	background-size: cover ;
