}
.gallery__item-imginner1:hover{
	opacity: .5; 
	filter: blur(0.2rem);
	background-image: url("../img/Projects/Cms_Numenor.png");
}
.gallery__item-imginner1:hover p, .gallery__item-imginner2:hover p, .gallery__item-imginner3:hover p, .gallery__item-imginner4:hover p{
	display: none; 
}
.gallery__item-imginner2:hover{
