}
.gallery__item-caption {
	grid-area: 1 / 1 / 4 / 3;
	display: grid;
	grid-template-areas: 'gallery-number gallery-title' 
						 'gallery-link ...'
						 'gallery-link gallery-tags' ;
	grid-template-columns: 8rem auto;
	grid-template-rows: 8rem auto 3rem;
}
.gallery__item-number {
	grid-area: gallery-number;
	font-size: 6rem;
	font-size: clamp(2.5rem,9vw,6.5rem);
	justify-self: end;
	padding-right: 2rem;
	color: var(--color-gallery-number);
}
.gallery__item-title {
	font-family: halyard-display;
	font-size: 3vw;
	line-height: 0.8;
	margin: 0 10vw 0 0;
	text-transform: initial;
	color: var(--color-gallery-title);
}
.gallery__item-link {
	grid-area: gallery-link;
	align-self: end;
	font-size: 1.5rem;
	background: var(--color-gallery-link-bg);
	color: var(--color-gallery-link);
	width: 120px;
	height: 120px;
	border-radius: 50%;
	position: relative;
	display: flex;
	justify-content: center;
	align-items: center;
	cursor: pointer;
	text-decoration: none; 
}
.gallery__item-link-disabled {
	pointer-events: none;
	grid-area: gallery-link;
	align-self: end;
	font-size: 1.5rem;
	background: var(--color-gallery-link-bg);
	color: var(--color-gallery-link);
	width: 120px;
	height: 120px;
	border-radius: 50%;
	position: relative;
	display: flex;
	justify-content: center;
	align-items: center;
	cursor: pointer;
	text-decoration: none; 
}
.gallery__item-link:focus,
.gallery__item-link:hover {
	background: var(--color-gallery-link-bg-hover);
	color: var(--color-gallery-link-hover);
	text-decoration: none;
}
.gallery__item-tags {
	grid-area: gallery-tags;
	color: var(--color-gallery-tags);
	margin-top: 100px; 
	justify-self: center;
	font-size: 3rem;
	display: grid;
	grid-auto-flow: column;
	cursor: pointer;
	font-weight: 800;
	align-self: center;
}
.gallery__text {
	font-size: 20vw;
	line-height: 0.8;
	margin: 0 10vw 0 0;
	text-transform: lowercase;
	color: var(--color-gallery-title);
	color: transparent;
	-webkit-text-stroke: 3px #978c77;
	text-stroke: 1px #978c77;
	-webkit-text-fill-color: transparent;
	text-fill-color: transparent;
}
.gallery__text-inner {
	display: block;  
}

/*********************************************** NAME & FIRSTNAME *******************************************************/

.gallery__text_name {
	font-size: 20vw;
	line-height: 0.9;
	margin: 0 10vw 0 0;
	text-transform: uppercase;
	color: var(--color-gallery-title);
	color: transparent;
	-webkit-text-stroke: 3px #978c77;
	text-stroke: 1px #978c77;
	-webkit-text-fill-color: transparent;
	text-fill-color: transparent;
}
.gallery__text-inner_name {
	display: block;
}
.gallery__text-inner_firstname{
	display: block;
}

/******************************** WORKS ***************************************/

.gallery__content-project{
	position: absolute;
	width: 100%;
	height: 60%;
	bottom: -50%; 
	padding: 20px; 
	padding-top: 90px; 
	text-align: center;
	transition: 0.5s;
}
.gallery__item-img:hover  .gallery__content-project {
	bottom: 0; 
	pointer-events: none;
}
.gallery__content-project h2{
	margin: 0 0 10px;
 	padding: 0;
 	color: #000;
 	font-size: 25px;
}
.gallery__content-project p{
	white-space: normal;
 	color: #000;
 	font-size: 20px;
}

/******************* STACK  *********************/

.gallery__item-stack {
	margin: 0 3vw;
	display: grid;
	grid-template-areas: '... ...' 
						 '... gallery-image'
						 '... ...' ;
	grid-template-columns: 8rem 21vmax;
	grid-template-rows: 8rem 28vmax 3rem;
}
.gallery__square{
	position: relative;
	grid-area: gallery-image;
	width: 400px;
	height: 100%;
	display: flex; 
	justify-content: center;
	align-items: center;
}
.square{
	position: relative; 
	width: 400px;
	height: 400px; 
	display: flex; 
	justify-content: center;
	align-items: center; 
}
.square span:nth-child(1) {
	position: absolute; 
	top: 0;
	left: 0; 
	width: 100%;
	height: 100%;
	border: 2px solid #978c77; 
	border-radius: 38% 62% 63% 37% / 41% 44% 56% 59%; 
	transition: 0.5s;
	animation: animate 6s linear infinite;
}
.square:hover span:nth-child(1) {
	border: none;
	background: rgba(0, 0, 0, 0.8);
}
.square span:nth-child(2) {
	position: absolute; 
	top: 0;
	left: 0; 
	width: 100%;
	height: 100%;
	border: 2px solid #978c77; 
	border-radius: 38% 62% 63% 37% / 41% 44% 56% 59%; 
	transition: 0.5s;
	animation: animate 4s linear infinite;
}
.square:hover span:nth-child(2) {
	border: none;
	background: rgba(0, 0, 0, 0.8);
}
.square span:nth-child(3) {
	position: absolute; 
	top: 0;
	left: 0; 
	width: 100%;
	height: 100%;
	border: 2px solid #978c77; 
	border-radius: 38% 62% 63% 37% / 41% 44% 56% 59%; 
	transition: 0.5s;
	animation: animate2 10s linear infinite;
}
.square:hover span:nth-child(3) {
	border: none;
	background: rgba(0, 0, 0, 0.8);
}

@keyframes animate {
	0%{
		transform: rotate(0deg);
	}
	100%{
		transform: rotate(360deg);
	}
}
@keyframes animate2 {
	0%{
		transform: rotate(360deg);
	}
	100%{
		transform: rotate(0deg);
	}
}

.content-stack{
	position: relative;
	padding: 40px 60px; 
	color: #000; 
	text-align: center;
	transition: 0.5s;
	z-index: 1000;
	white-space: normal;
}
.square:hover .content-stack{
	color: #fff; 
}


